// Runtime: 576 ms
// Memory Usage: 180.5 MB

class Solution {
public:
    vector<string> pubwords;
    string pubs;
    
    bool checkstring(int start, string t) {
        int l = t.length();
        for (int i=0;i<l;i++)
            if (pubs[start+i]!=t[i])
                return false;
        return true;
    }
    
    bool checkall(int start, int u) {
        int l= pubwords[0].length();
        bool used[1000]={false};
        used[u]=true;
        int num=1;
        bool k=true;
        while (k)
        {
            k=false;
            start=start+l;
            for (int i=0;i<pubwords.size();i++)
                if (!used[i] && checkstring(start, pubwords[i]))
                {
                    k=true;
                    used[i]=true;
                    num++;
                    break;
                } 
        }
        return (num==pubwords.size());
    }
     
    vector<int> findSubstring(string s, vector<string>& words) {
        vector<int> answer;
        pubwords = words;
        pubs = s;
        
        if (s == "")
            return answer;
        
        
        if (words.size()==0)
            return answer;
        
        int l = words[0].length();
        
        for (int i=0;i<=int(s.length()-words.size()*l);i++) 
            for (int j=0;j<words.size();j++)
                if (checkstring(i,words[j]))
                    if (checkall(i,j))
                    {
                        answer.push_back(i);
                        break;
                    }
        
        return answer;
    }
};